package com.junnew.features.main.setting.components

import androidx.compose.foundation.background
import androidx.compose.foundation.border
import androidx.compose.foundation.layout.Box
import androidx.compose.foundation.layout.Column
import androidx.compose.foundation.layout.Row
import androidx.compose.foundation.layout.fillMaxSize
import androidx.compose.foundation.layout.height
import androidx.compose.foundation.layout.width
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.material3.MaterialTheme
import androidx.compose.material3.Text
import androidx.compose.runtime.Composable
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.draw.clip
import androidx.compose.ui.layout.ContentScale
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.tooling.preview.Preview
import androidx.compose.ui.unit.dp
import coil3.compose.AsyncImage
import com.junnew.design_system.component.box.IBox
import com.junnew.design_system.theme.appColors

@Composable
fun HeaderSetting() {

    val colors = MaterialTheme.appColors
    val text = MaterialTheme.typography

    val shape = RoundedCornerShape(70.dp)

    Row(verticalAlignment = Alignment.CenterVertically) {
        Box(modifier = Modifier
            .height(70.dp)
            .width(70.dp)
            .clip(shape)
            .border(width = 1.dp, color = colors.white, shape = shape)
            .background(colors.purpleBlueOpa)
        ){
            AsyncImage(
                model = "data:image/jpeg;base64,",
                contentDescription = null,
                contentScale = ContentScale.Crop,
                modifier = Modifier.fillMaxSize(),
                alignment = Alignment.Center
            )
        }
        IBox(width = 10.dp)
        Box(modifier = Modifier.weight(1f)){
            Column {
                Text("User Name", style = text.headlineMedium.copy(
                    colors.white
                ))
                IBox(width = 10.dp)
                Text("Edit Profile",style = text.bodyMedium.copy(
                    colors.white,
                    fontWeight = FontWeight.W300
                ))
            }
        }
    }
}


@Preview
@Composable
fun PreViewHeaderSetting() {
    HeaderSetting()
}